import React, { createContext } from "react";
import all_product from "../Components/Assets/Frontend_Assets/all_product";

export const ShopContext = createContext(null);

const ShopContextProvider = (props) => {
  const contextValue = { all_product };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;

/*
ה-Context ב-React מספק דרך להעביר נתונים (כמו ערכים והגדרות) דרך עץ הרכיבים (component tree) מבלי הצורך להעביר props באופן ידני דרך כל רמה של העץ. השימוש ב-Context שימושי במיוחד כאשר יש לך נתונים שהם "גלובליים" לאפליקציה שלך, כמו נתוני משתמש, הגדרות ערכת נושא, נתוני שפה, וכדומה.

